package CH_26_String;
// Prime number
public class Example_13a {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	int a =125;
	
	for (int i =2; i<=a ;i++)
	{
		for (int j = 2; j<=i; j++)
		{
			if(j==i)  
			{
				System.out.println(i);
			}
			if (i%j==0)  // if reminder is zero(0) then it is not prime number.
			{
				break;
			}}}}

     }
